import React from 'react';
import { Typography } from '@mui/material';
import Grid from '@mui/material/Grid';
import Divider from '@mui/material/Divider';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select'
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';
import AddBoxIcon from '@mui/icons-material/AddBox';
import CancelIcon from '@mui/icons-material/Cancel';

const ToDoList = () => {
    const [age, setAge] = React.useState('');
    const [level, setLevel] = React.useState('');

    const handleChangeAge = (event) => {
        setAge(event.target.value);
    };

    const handleChangeLevel = (event) => {
        setLevel(event.target.value);
    };

    return (
        <Grid container>
            <Grid container justifyContent="center" xs={12} >
                <Typography variant='h4'>Quản lý công việc</Typography>

            </Grid>
            <Grid xs={12} >
                <Divider />
            </Grid>
            <Grid container xs={12} mt={3}>
                <Grid 
                    container 
                    xs={6} 
                    columns={12} 
                    justifyContent="center"
                    alignItems="center"
                >
                    <Grid item xs={6}>
                        <TextField 
                            id="outlined-basic" 
                            color="secondary"
                            label="Tìm kiếm" 
                            variant="outlined" 
                            size="small"
                            placeholder="Nhập tên công việc"
                        />
                        <Button variant="contained"  color="secondary" endIcon={<SearchIcon />}>Tìm</Button>
                    </Grid>
                    <Grid item xs={3} mr={3}>
                        <FormControl fullWidth>
                            <InputLabel id="demo-simple-select-label">Sắp xếp theo</InputLabel>
                            <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                value={age}
                                label="Sắp xếp theo"
                                onChange={handleChangeAge}
                                size="small"
                            >
                                <MenuItem value={10}>Ten</MenuItem>
                                <MenuItem value={20}>Twenty</MenuItem>
                                <MenuItem value={30}>Thirty</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                </Grid>
                <Grid item xs={6}>
                    <Button variant="contained" endIcon={<AddIcon />}  color="secondary" fullWidth>Thêm công việc</Button>
                </Grid>
                <Grid container xs={12} mt={2}>
                    <Grid item xs={6} />
                    <Grid container xs={6} spacing={2}>
                        <Grid item xs={5}>
                            <TextField
                                variant="outlined" 
                                size="small"
                                placeholder="Nhập tên công việc"
                                color="secondary"
                                fullWidth
                            />
                        </Grid>
                        <Grid item xs={3}>
                            <FormControl fullWidth>
                                <InputLabel id="demo-simple-select-label">Độ ưu tiên</InputLabel>
                                <Select
                                    labelId="demo-simple-select-label1"
                                    id="demo-simple-select1"
                                    value={level}
                                    label="Sắp xếp theo"
                                    onChange={handleChangeLevel}
                                    size="small"
                                >
                                    <MenuItem value={10}>Dễ</MenuItem>
                                    <MenuItem value={20}>Trung bình</MenuItem>
                                    <MenuItem value={30}>Khoai</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>
                        <Grid item xs={2}>
                            <Button variant="contained" endIcon={<AddBoxIcon />}  color="success" fullWidth>Thêm</Button>
                        </Grid>
                        <Grid item xs={2}>
                            <Button variant="outlined" endIcon={<CancelIcon />}  color="warning" fullWidth>Hủy</Button>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
    )
}

export default ToDoList;
